import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int A = scanner.nextInt();
        int B = scanner.nextInt();
        int C = scanner.nextInt();
        int N = scanner.nextInt();

        char charUpper = 'A';
        char charLower = 'a';
        char charNew = 'C';
        int num = 1;
        int count;

        int i = 0;
        while (i < N) {
            count = A;
            while (count > 0 && i < N) {
                if (charUpper == 'A') {
                    charUpper = 'B';
                    System.out.print("A");
                } else {
                    charUpper = 'A';
                    System.out.print("B");
                }
                count--;
                i++;
            }

            count = B;
            while (count > 0 && i < N) {
                if (charLower == 'a') {
                    charLower = 'b';
                    System.out.print("a");
                } else {
                    charLower = 'a';
                    System.out.print("b");
                }
                count--;
                i++;
            }

            count = C;
            while (count > 0 && i < N) {
                if (num == 1) {
                    System.out.print(num);
                    num++;
                } else {
                    System.out.print(num);
                    num--;
                }
                count--;
                i++;
            }

            if (A + B + C == 0) {
                if (charNew == 'C') {
                    charNew = 'D';
                    System.out.print("C");
                } else {
                    charNew = 'C';
                    System.out.print("D");
                }
                i++;
            }
        }
    }
}

import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner input = new Scanner(System.in);
        String word = input.nextLine();
        input.close();
        int numInsertions = countInsertions(word);
        System.out.println(numInsertions);
    }

    public static int countInsertions(String word) {
        char[] letters = word.toCharArray();
        int numInsertions = 0;
        for (int i = 0; i < letters.length - 2; i++) {
            if (isProblematic(letters[i], letters[i + 1], letters[i + 2])) {
                numInsertions++;
                i++;
            }
        }
        return numInsertions;
    }

    public static boolean isProblematic(char c1, char c2, char c3) {
        return (isVowel(c1) && isVowel(c2) && isVowel(c3))
                || (isConsonant(c1) && isConsonant(c2) && isConsonant(c3));
    }

    public static boolean isVowel(char c) {
        return "aeiouy".indexOf(Character.toLowerCase(c)) != -1;
    }

    public static boolean isConsonant(char c) {
        return !isVowel(c) && Character.isLetter(c);
    }
}

