import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner input = new Scanner(System.in);
        String word = input.nextLine();
        input.close();
        int numInsertions = countInsertions(word);
        System.out.println(numInsertions);
    }

    public static int countInsertions(String word) {
        char[] letters = word.toCharArray();
        int numInsertions = 0;
        for (int i = 0; i < letters.length - 2; i++) {
            if (isProblematic(letters[i], letters[i + 1], letters[i + 2])) {
                numInsertions++;
                i++;
            }
        }
        return numInsertions;
    }

    public static boolean isProblematic(char c1, char c2, char c3) {
        return (isVowel(c1) && isVowel(c2) && isVowel(c3))
                || (isConsonant(c1) && isConsonant(c2) && isConsonant(c3));
    }

    public static boolean isVowel(char c) {
        return "aeiouy".indexOf(Character.toLowerCase(c)) != -1;
    }

    public static boolean isConsonant(char c) {
        return !isVowel(c) && Character.isLetter(c);
    }
}

